Select m.name, a.first_name || " " || a.last_name as full_name from actors as a join roles as r on r.actor_id = a.id join movies as m on r.movie_id = m.id join movies_genres as mg on mg.movie_id = m.id and mg.genre = 'Drama' where m.id in (select bacon_m.id from movies as bacon_m join roles as bacon_r on bacon_r.movie_id = bacon_m.id join actors as bacon_a on bacon_r.actor_id = bacon_a.id where bacon_a.first_name = 'Kevin' and bacon_a.last_name = 'Bacon') and full_name != 'Kevin Bacon' order by m.name;
